import AppLayout from "../component/AppLayout";
import { Input } from "antd";
//import * as dotenv from "dotenv";
import { Configuration, OpenAIApi } from "openai";
import { useState } from "react";

const Home = () => {
  const [model, setModel] = useState("gpt-3.5-turbo");
  const [answer, setAnswer] = useState("");

  // dotenv.config();
  const configuration = new Configuration({
    // apiKey: process.env.OPENAI_API_KEY,
    apiKey: "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",
  });
  const openai = new OpenAIApi(configuration);

  async function ask(content, model) {
    const res = await openai.createChatCompletion({
      model: model,
      messages: [{ role: "user", content: content }],
    });

    setAnswer(res.data.choices[0].message?.content);
    console.log(answer);
  }

  const onSearch = (value) => {
    ask(value, model);
  };

  return (
    <AppLayout version="3.5-turbo">
      <Input.Search
        placeholder="input search text"
        onSearch={onSearch}
        allowClear
        enterButton="Search"
        size="large"
      ></Input.Search>
      <Input.TextArea
        rows={4}
        placeholder="Responses from GPT will be displayed here."
        readOnly
        value={answer}
      ></Input.TextArea>
    </AppLayout>
  );
};

export default Home;
